// Map
// it returns new modifie d array
let arr = [
    { name: "John", age: 25 },
    { name: "Jane", age: 22 }
];

let newArr = arr.map((ele) => {
    return {
        newName: ele.name
        + "Doe",  // Concatenates "Doe" to each element's name
        newAge: ele.age + 10        // Adds 10 to each element's age
    };
});
console.log(newArr);

